contracts/Campaign.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;
import "./Organization.sol";


error Campaign__NotEnoughEthEntered();
error Campaign__AlreadyEnrolled();
error Campaign__MaximumCampaignsReached();

contract Campaign {
    address public owner;
    uint256 public minimumContribution;
    mapping(address => bool) public contributers;
   uint256 constant public minimumDisburseAllowed = 1 ether; // hardcoded minimum disburse amount
    uint256 public contributersCount;
    //mapping to store organizations
    address[] public organizations;

    

    //make a struct
    struct CampaignDetails {
        string name;
        string description;
       // string campaignImageHash;
        string tagLine;
        string[] tags;
        uint256 likes;
    }
    CampaignDetails public details;
 

    constructor(uint256 minimum, address creator
    , string memory name, string memory description, string memory tagLine, string[] memory tags
    ) {
        owner = creator;
        minimumContribution = minimum;
        //intialize campaignDetails
        details.name = name;
        details.description = description;
        details.tagLine = tagLine;
        details.tags = new string[](2);
        details.tags = tags;
        details.likes = 0;

    }

    function fund() public payable {
        //require(msg.value > minimumContribution);
        contributers[msg.sender] = true;
        contributersCount++;
    }

    //creating modifiers
    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    modifier onlyContributer() {
        require(contributers[msg.sender] == true);
        _;
    }

    //function to get balance in the contract
    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }

    //get minimum ammount
    function getMinimumContribution() public view returns (uint256) {
        return minimumContribution;
    }
    //function to withdraw money to specific address
    function withdrawMoney(address payable recipient) public {
        recipient.transfer(address(this).balance);
    }

    //function to enroll organization to this campaign
    function enrollOrganization(address organization) public onlyOwner {
        //check if organzation is already enrolled in this campaign
        for (uint256 i = 0; i < organizations.length; i++) {
            if (organizations[i] == organization) {
                revert Campaign__AlreadyEnrolled();
            }
        }

        //check if organization is enrolled in 3 campaigns
         if (Organization(organization).getCampaignsCount() >= 3) {
            revert Campaign__MaximumCampaignsReached();
         }
        organizations.push(organization);
    }

    //disburse all funds to all organizations equally
    function disburseFunds() public onlyOwner {
        //check if enough funds are available
        if (getBalance() < minimumDisburseAllowed) {
            revert Campaign__NotEnoughEthEntered();
        }

        //equal share of funds
        uint256 minimumDisburseAmount = getBalance() / organizations.length;
        
        
        //disburse funds
        for (uint256 i = 0; i < organizations.length; i++) {
            Organization(organizations[i]).contribute{value: minimumDisburseAmount}();
        }
    }

    //function to get details
    function getDetails() public view returns (CampaignDetails memory) {
    
        return details;
    }
}

contracts/Organization.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

contract Organization{
    address public owner;
    string public name;
    string public description;
    string public website;
    string public logo;
    uint256 public minimumContribution;
    mapping(address => bool) public contributers;
    uint256 public contributersCount;
    uint256 public campaignsCount;
    address[] public campaigns;


    //make a struct
    struct OrganizationDetails {

        string name;
        string description;
        string website;
        string logo;
     
    }
    OrganizationDetails public details;


    constructor(string memory _name, string memory _description, string memory _website, string memory _logo, address creator) {
        owner = creator;
        details.name = _name;
        details.description = _description;
        details.website = _website;
        details.logo = _logo;
    }

    function contribute() public payable {
        //require(msg.value > minimumContribution);
        contributers[msg.sender] = true;
        contributersCount++;
    }

    //creating modifiers
    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    modifier onlyContributer() {
        require(contributers[msg.sender] == true);
        _;
    }

    //function to get balance in the contract
    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }

    //get minimum ammount
    function getMinimumContribution() public view returns (uint256) {
        return minimumContribution;
    }
    //function to withdraw money to specific address
    function withdrawMoney(address payable recipient, uint256 amount) public onlyOwner {
        require(amount <= getBalance());
        recipient.transfer(amount);
    }

    //function to withdraw money to specific address
    function withdrawAllMoney(address payable recipient) public onlyOwner {
        recipient.transfer(getBalance());
    }

    //function to get all contributers
    function getAllContributers() public view returns (address[] memory) {
        address[] memory contributersArray = new address[](contributersCount);
        uint256 counter = 0;
        for (uint256 i = 0; i < contributersCount; i++) {
            contributersArray[counter] = contributersArray[i];
            counter++;
        }
        return contributersArray;
    }

    //get details of organization
    function getDetails() public view returns (OrganizationDetails memory) {
        return details;
    }

    //get campaign count
    function getCampaignsCount() public view returns (uint256) {
        return campaignsCount;
    }
    

    //increment total campaigns
    function incrementCampaignsCount() public {
        campaignsCount++;
    }

    //get all campaigns
    function getAllCampaigns() public view returns (address[] memory) {
        return campaigns;
    }

    //enroll in campaign
    function enrollInCampaign(address campaignAddress) public {
        //make sure campaign count is less than 3 before pushing
        require(campaignsCount < 3);
        campaigns.push(campaignAddress);
    }
}

contracts/CampaignFactory.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "./Campaign.sol";

contract CampaignFactory {
    address[] public campaigns;

    function createCampaign(uint256 minimumContribution
    , string memory name, string memory description, string memory tagLine, string[] memory tags
    ) public {
        address newCampaign = address(new Campaign(minimumContribution, msg.sender, name, description, tagLine, tags));
        campaigns.push(newCampaign);
    }

    //get all campaigns
    function getAllCampaigns() public view returns (address[] memory) {
        return campaigns;
    }

}

